::ng-deep [type="radio"]:checked,
::ng-deep [type="radio"]:not(:checked) {
  position: absolute;
  opacity: 0;
  /* left: -9999px; */
}
::ng-deep [type="radio"]:checked + label,
::ng-deep [type="radio"]:not(:checked) + label {
  font-family: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont,
    "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif,
    "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
  font-weight: bold;
  position: relative;
  padding-left: 28px;
  cursor: pointer;
  line-height: 20px;
  display: inline-block;
  color: #374151;
  margin: 0.5rem;
}
::ng-deep [type="radio"]:checked + label:before,
::ng-deep [type="radio"]:not(:checked) + label:before {
  content: "";
  position: absolute;
  left: 0;
  top: 0;
  width: 18px;
  height: 18px;
  border: 2px solid #fbbf24;
  border-radius: 100%;
}
::ng-deep [type="radio"]:checked + label:after,
::ng-deep [type="radio"]:not(:checked) + label:after {
  content: "";
  width: 12px;
  height: 12px;
  background: #374151;
  position: absolute;
  top: 5px;
  left: 5px;
  border-radius: 100%;
  -webkit-transition: all 0.2s ease;
  transition: all 0.2s ease;
}
::ng-deep [type="radio"]:not(:checked) + label:after {
  opacity: 0;
  -webkit-transform: scale(0);
  transform: scale(0);
}
::ng-deep [type="radio"]:checked + label:after {
  opacity: 1;
  -webkit-transform: scale(1);
  transform: scale(1);
}

