.spinner {
  display: flex;
  align-items: center;
  justify-content: center;
}

.spinner--horizontal {
  flex-direction: row;
}

.spinner--vertical {
  flex-direction: column;
}

.spinner__text {
  margin: 0.75rem;
  font-weight: bold;
  font-family: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont,
    "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif,
    "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
}

.loader,
.loader:after {
  border-radius: 50%;
  width: 3em;
  height: 3em;
}
.loader {
  font-size: 10px;
  position: relative;
  border-top: 0.8em solid rgba(0, 0, 0, 0.2);
  border-right: 0.8em solid rgba(0, 0, 0, 0.2);
  border-bottom: 0.8em solid rgba(0, 0, 0, 0.2);
  border-left: 0.8em solid #374151;
  -webkit-transform: translateZ(0);
  -ms-transform: translateZ(0);
  transform: translateZ(0);
  -webkit-animation: load8 1s infinite linear;
  animation: load8 1s infinite linear;
}
@-webkit-keyframes load8 {
  0% {
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg);
  }
  100% {
    -webkit-transform: rotate(360deg);
    transform: rotate(360deg);
  }
}
@keyframes load8 {
  0% {
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg);
  }
  100% {
    -webkit-transform: rotate(360deg);
    transform: rotate(360deg);
  }
}

.cis-ellipsis {
  display: inline-block;
  position: relative;
  width: 80px;
  height: 80px;
}
.cis-ellipsis div {
  position: absolute;
  top: 33px;
  width: 13px;
  height: 13px;
  border-radius: 50%;
  background: #374151;
  animation-timing-function: cubic-bezier(0, 1, 1, 0);
}
.cis-ellipsis div:nth-child(1) {
  left: 8px;
  animation: cis-ellipsis1 0.6s infinite;
}
.cis-ellipsis div:nth-child(2) {
  left: 8px;
  animation: cis-ellipsis2 0.6s infinite;
}
.cis-ellipsis div:nth-child(3) {
  left: 32px;
  animation: cis-ellipsis2 0.6s infinite;
}
.cis-ellipsis div:nth-child(4) {
  left: 56px;
  animation: cis-ellipsis3 0.6s infinite;
}
@keyframes cis-ellipsis1 {
  0% {
    transform: scale(0);
  }
  100% {
    transform: scale(1);
  }
}
@keyframes cis-ellipsis3 {
  0% {
    transform: scale(1);
  }
  100% {
    transform: scale(0);
  }
}
@keyframes cis-ellipsis2 {
  0% {
    transform: translate(0, 0);
  }
  100% {
    transform: translate(24px, 0);
  }
}

